import React, { useState } from 'react';
import { 
  Card, 
  Row, 
  Col, 
  Button, 
  Form, 
  Input, 
  InputNumber, 
  Modal, 
  Typography, 
  Upload, 
  message 
} from 'antd';
import { PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';

const { Title, Text } = Typography;
const { TextArea } = Input;

const Projects = () => {
  const [projects, setProjects] = useState([
    { 
      id: 1, 
      title: 'Project 1', 
      description: 'Description of project 1', 
      year: 2024,
      imageUrl: 'https://via.placeholder.com/300x200?text=Project+1'
    },
    { 
      id: 2, 
      title: 'Project 2', 
      description: 'Description of project 2', 
      year: 2023,
      imageUrl: 'https://via.placeholder.com/300x200?text=Project+2'
    }
  ]);

  const [isModalVisible, setIsModalVisible] = useState(false);
  const [editingProject, setEditingProject] = useState(null);
  const [form] = Form.useForm();

  const showModal = (project = null) => {
    setEditingProject(project);
    if (project) {
      form.setFieldsValue(project);
    } else {
      form.resetFields();
    }
    setIsModalVisible(true);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    setEditingProject(null);
    form.resetFields();
  };

  const onFinish = (values) => {
    if (editingProject) {
      // Update existing project
      setProjects(projects.map(p => 
        p.id === editingProject.id ? { ...p, ...values } : p
      ));
      message.success('Project updated successfully');
    } else {
      // Add new project
      const newProject = {
        id: Date.now(),
        ...values,
        imageUrl: values.imageUrl || 'https://via.placeholder.com/300x200?text=New+Project'
      };
      setProjects([...projects, newProject]);
      message.success('Project added successfully');
    }
    handleCancel();
  };

  const deleteProject = (id) => {
    setProjects(projects.filter(p => p.id !== id));
    message.success('Project deleted successfully');
  };

  const beforeUpload = (file) => {
    const isImage = file.type.startsWith('image/');
    if (!isImage) {
      message.error('You can only upload image files!');
    }
    return isImage;
  };

  return (
    <div className="projects-page" style={{ padding: '24px' }}>
      <Title level={1} style={{ textAlign: 'center', marginBottom: '24px' }}>My Projects</Title>
      
      <Button 
        type="primary" 
        icon={<PlusOutlined />} 
        onClick={() => showModal()}
        style={{ marginBottom: '24px' }}
      >
        Add New Project
      </Button>

      <Row gutter={[16, 16]}>
        {projects.map((project) => (
          <Col key={project.id} xs={24} sm={12} md={8} lg={6}>
            <Card 
              cover={
                <img 
                  alt={project.title} 
                  src={project.imageUrl} 
                  style={{ height: '160px', objectFit: 'cover' }}
                />
              }
              actions={[
                <EditOutlined key="edit" onClick={() => showModal(project)} />,
                <DeleteOutlined key="delete" onClick={() => deleteProject(project.id)} />
              ]}
            >
              <Card.Meta
                title={project.title}
                description={
                  <>
                    <Text type="secondary">{project.year}</Text>
                    <p style={{ marginTop: '8px' }}>{project.description}</p>
                  </>
                }
              />
            </Card>
          </Col>
        ))}
      </Row>

      {/* Project Form Modal */}
      <Modal 
        title={editingProject ? "Edit Project" : "Add New Project"} 
        visible={isModalVisible} 
        onCancel={handleCancel}
        footer={null}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={onFinish}
        >
          <Form.Item
            name="title"
            label="Project Title"
            rules={[{ required: true, message: 'Please input project title!' }]}
          >
            <Input placeholder="Enter project title" />
          </Form.Item>

          <Form.Item
            name="year"
            label="Year"
            rules={[{ required: true, message: 'Please input project year!' }]}
          >
            <InputNumber 
              style={{ width: '100%' }} 
              min={2000} 
              max={new Date().getFullYear()} 
              placeholder="Enter project year" 
            />
          </Form.Item>

          <Form.Item
            name="description"
            label="Description"
            rules={[{ required: true, message: 'Please input project description!' }]}
          >
            <TextArea rows={4} placeholder="Enter project description" />
          </Form.Item>

          <Form.Item
            name="imageUrl"
            label="Image URL"
            extra="Paste an image URL or upload an image"
          >
            <Input placeholder="https://example.com/image.jpg" />
          </Form.Item>

          <Form.Item>
            <Upload
              name="image"
              listType="picture-card"
              showUploadList={false}
              beforeUpload={beforeUpload}
              onChange={info => {
                if (info.file.status === 'done') {
                  // In a real app, you would upload the image to a server
                  // and get the URL, then set it in the form
                  form.setFieldsValue({
                    imageUrl: URL.createObjectURL(info.file.originFileObj)
                  });
                }
              }}
            >
              <div>
                <PlusOutlined />
                <div style={{ marginTop: 8 }}>Upload</div>
              </div>
            </Upload>
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" style={{ marginRight: '8px' }}>
              {editingProject ? 'Update' : 'Add'} Project
            </Button>
            <Button onClick={handleCancel}>
              Cancel
            </Button>
          </Form.Item>
        </Form>
      </Modal>

      <div style={{ textAlign: 'center', marginTop: '40px', color: '#666' }}>
        Portfolio Â©{new Date().getFullYear()} Created by You
      </div>
    </div>
  );
};

export default Projects;
